mapper.select:
 	 		SELECT qna_no, user_no, title, state, write_date, content, kinds, answer
			FROM qna
			WHERE qna_no = #{qnaNo}

mapper.update:
UPDATE qna
SET kinds = kinds + 1
WHERE qna_no = #{qnaNo}

mapper.select:
INSERT INTO qna (qna_no, user_no, title, state, write_date, content, kinds, answer
VALUES (qna_no_seq.NEXTVAL, #{userNo}, #{title}, #{state}, sysdate, #{content},
		#{kinds}, #{answer}
WHERE user_no = #{userNo}
